import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles'

import './App.css';

import { Grid, MenuItem, FormControl, InputLabel, Select } from '@material-ui/core'
import Number from './Components/Number'
import AddButton from './Components/Buttons/AddButton'
import SubtractButton from './Components/Buttons/SubtractButton'
import SubDisabledButton from './Components/Buttons/SubDisabledButton'
import ResetButton from './Components/Buttons/ResetButton'
import ResetDisabledButton from './Components/Buttons/ResetDisabledButton'

const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
    DmitriSwank: {
        fontFamily: 'Dimitri Swank',
    },
    Roboto: {
        fontFamily: 'Roboto',
    }
}));

function App() {
  const [state, setState] = useState(0)
  const [style, setStyle] = useState('DimitriSwank')
  const classes = useStyles()

  const addHandler = () => {
    setState(state + 1)
  }

  const subHandler = () => {
    setState(state - 1)
  }

  const resetHandler = () => {
    setState(0)
  }

  const changeFontHandler = (event) => {
    setStyle(event.target.value)
  }

  return (
    <Grid className="App" container>
      <Grid item xs={12} className='BigNum'>
        <Number number={state} />
      </Grid>
      <Grid container spacing={3} className="ButtonBar">
        <Grid item>
          <AddButton clicked={addHandler} />
        </Grid>
        <Grid item >
          {state > 0 ? 
            <SubtractButton clicked={subHandler} />  : 
            <SubDisabledButton />
            }     
        </Grid>
        <Grid item>
          { state > 0 ?
          <ResetButton clicked={resetHandler}/> :
          <ResetDisabledButton />
          }
          </Grid>
      </Grid>
      <Grid container spacing={3} className="StyleBar">
          <Grid item>
              <FormControl className={classes.formControl}>
                <InputLabel id='changeFont'>
                    Change Font
                </InputLabel>
                <Select
                    labelId='changeFont'
                    id='font'
                    value={style}
                    onChange={changeFontHandler}
                    >
                    <MenuItem value={'Roboto'}>Roboto</MenuItem>
                    <MenuItem value={'DimitriSwank'}>Dimitri Swank</MenuItem>
                    <MenuItem value={'OrelegaOne'}>Orelega One</MenuItem>
                    </Select>
              </FormControl>
          </Grid>
      </Grid>
    </Grid>
  );
}

export default App;
