import React, { useState } from 'react'

import './App.css';

import { Grid, MenuItem } from '@material-ui/core'
import Number from './Components/Number'
import AddButton from './Components/Buttons/AddButton'
import SubtractButton from './Components/Buttons/SubtractButton'
import SubDisabledButton from './Components/Buttons/SubDisabledButton'
import ResetButton from './Components/Buttons/ResetButton'
import ResetDisabledButton from './Components/Buttons/ResetDisabledButton'
//import FontField from './Components/FontField'
import { FormControl, InputLabel, Select } from '@material-ui/core'

const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
}));

function App() {
  const [state, setState] = useState(0)
  const [style, setStyle] = useState('Dimitri Swank')

  const addHandler = () => {
    setState(state + 1)
  }

  const subHandler = () => {
    setState(state - 1)
  }

  const resetHandler = () => {
    setState(0)
  }

  const changeFontHandler = () => {
    setStyle(target.value)
  }

  return (
    <Grid className="App" container>
      <Grid item xs={12} className="BigNum" style={{ fontFamily: {style}}}>
        <Number number={state} />
      </Grid>
      <Grid container spacing={3} className="ButtonBar">
        <Grid item>
          <AddButton clicked={addHandler} />
        </Grid>
        <Grid item >
          {state > 0 ? 
            <SubtractButton clicked={subHandler} />  : 
            <SubDisabledButton />
            }     
        </Grid>
        <Grid item>
          { state > 0 ?
          <ResetButton clicked={resetHandler}/> :
          <ResetDisabledButton />
          }
          </Grid>
      </Grid>
      <Grid container spacing={3} className="StyleBar">
          <Grid item>
          <FormControl className={classes.formControl}>
            <InputLabel id='changeFont'>
                Change Font
            </InputLabel>
            <Select
                labelId='changeFont'
                id='font'
                value={props.value}
                onChange={props.changeStyle}
                >
                <MenuItem value={'Roboto'}>Roboto</MenuItem>
                <MenuItem value={'Dimitri Swank'}>Dimitri Swank</MenuItem>
                <MenuItem value={'Orelega One'}>Orelega One</MenuItem>
                </Select>
        </FormControl>
          </Grid>
      </Grid>
    </Grid>
  );
}

export default App;
