import React, { useState } from 'react'

import { createMuiTheme, Grid, Paper, ThemeProvider } from '@material-ui/core'
import { deepPurple, purple } from '@material-ui/core/colors'
import './App.css';

import BigNumber from './Components/MainSections/BigNumber'
import ButtonBar from './Components/MainSections/ButtonBar'
import DarkToggle from './Components/MainSections/DarkToggle'
import FontSelector from './Components/MainSections/FontSelector'
import SizeSelector from './Components/MainSections/SizeSelector'
import ColorSelectorBar from './Components/MainSections/ColorSelectorBar'
import ColorPickDialog from './HOCs/ColorPickDialog'

function App() {
  const [state, setState] = useState(0)
  const [fontSizeState, setFontSizeState] = useState(100)
  const [fontStyle, setFontStyle] = useState('DimitriSwank')
  const [darkMode, setDarkMode] = useState(false)
  const [fontColor, setFontColor] = useState('')
  const [bkgroundColor, setBkGroundColor] = useState('')
  const [dialogOpen, setDialogOpen] = useState(false)

  const theme = createMuiTheme({
    palette: {
      primary: {
        main: deepPurple[500],
      },
      secondary: {
        main: purple[500],
      },
      type: darkMode ? 'dark' : 'light',
    },
  });



  const addHandler = () => {
    setState(state + 1)
  }

  const subHandler = () => {
    setState(state - 1)
  }

  const resetHandler = () => {
    setState(0)
  }

  const darkModeToggle = () => {
    setDarkMode(!darkMode)
    setFontColor('#0000000')
  }

  const changeFontHandler = (event) => {
    setFontStyle(event.target.value)
  }

  const valuetext = (value) => {
    return `${value}`
  }

  const setFontSizeHandler = (event, newValue) => {
    setFontSizeState(newValue)
  }

  const setFontColorHandler = (color) => {
    setFontColor(color.hex)
  }

  const setbkgroundHandler = (color) => {
    setBkGroundColor(color.hex)
  }

  const dialogToggle = () => {
    setDialogOpen(!dialogOpen)
  }

  return (
    <ThemeProvider theme={theme}>
      <Paper className="App" style={{background: {bkgroundColor}}}>
        <Grid container spacing={4} className='mainGrid'>

          <Grid container item spacing={4} className='topSection'>

            <BigNumber 
              color={fontColor} 
              size={fontSizeState} 
              fontFam={fontStyle} 
              state={state}
            />
            <ButtonBar 
              addClick={addHandler}
              subClick={subHandler}
              resetClick={resetHandler}
              state={state}
            />

          </Grid>

          <Grid container item spacing={3} className="StyleBar">

            <DarkToggle 
              mode={darkMode}
              clicked={darkModeToggle}
            />
            <FontSelector 
              font={ fontStyle }
              clicked={ changeFontHandler }
            />

          <Grid container item spacing={3}>

            <SizeSelector 
              aria={ valuetext }
              changed={ setFontSizeHandler }
            />
          </Grid>
            <ColorPickDialog 
              open={ dialogOpen }
              closed = {dialogToggle}
              setColor = {(e)=>setFontColorHandler(e)}
              onChange = {(e) => setbkgroundHandler(e)}


            />
            <ColorSelectorBar 
              fontChange={dialogToggle}
              bkgroundChange={dialogToggle}
              fontColor = {fontColor}
              fontChanged = {setFontColorHandler}
              bkgroundColor = {bkgroundColor}
              bkgroundChanged ={setbkgroundHandler}
            />
            </Grid>
      </Grid>
      </Paper>
    </ThemeProvider>
  );
}

export default App;
